	(206, 24, '1990年社会状况公众评价调查数据表', '<div align="center">\r\n	<span></span> \r\n	<div>\r\n		<span style="font-family:SimSun;color:#333333;font-size:14px;">&nbsp;</span> \r\n	</div>\r\n	<div align="center">\r\n		<span style="font-family:SimSun;color:#333333;font-size:14px;">发布日期：</span><span style="font-family:SimSun;color:#333333;font-size:14px;">1990-7-29</span><span style="font-family:SimSun;color:#333333;font-size:14px;">&nbsp;&nbsp;</span> \r\n	</div>\r\n	<div>\r\n		<span style="font-family:SimSun;color:#333333;font-size:14px;">&nbsp;</span> \r\n	</div>\r\n	<div>\r\n		<span><img style="width:524px;height:345px;" border="0" src="http://www.c-por.org/WebEditor/uploadfile/20110729014101172.jpg" width="525" height="344" /> </span>\r\n	</div>\r\n</div>\r\n<p align="center">\r\n	续表\r\n</p>\r\n<p align="center">\r\n	<img style="width:528px;height:434px;" border="0" src="http://www.c-por.org/WebEditor/uploadfile/20110729014118906.jpg" width="523" height="437" /> \r\n</p>\r\n<p align="center">\r\n	续表\r\n</p>\r\n<p align="center">\r\n	<img border="0" src="http://www.c-por.org/WebEditor/uploadfile/20110730221244991.jpg" /> \r\n</p>', '', 50, 1, 649177200, '', NULL, '', '', 35, 0, 0, 0, '', ''),
